<?php

namespace Bacloo\CrmBundle\Entity;

/**
 * LocationsslRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class LocationsslRepository extends \Doctrine\ORM\EntityRepository
{
	public function nblocsl()
	{
		$qb = $this->createQueryBuilder('f');
		$qb->select('COUNT(f.id) as nblocparc');
		$qb->where('f.etatloc = :etatloc')
			->setParameter('etatloc', 'En location');
		$nblocparc = $qb->getQuery()->getSingleScalarResult();

		return $nblocparc;
	}
}
