	public function camensuel($search, $dStart, $dEnd)
	{
		if($search == 0)
		{//echo 'laaaa';echo $dEnd;echo $dStart;
			$qb = $this->createQueryBuilder('a');
			$qb->where('YEARMONTH(a.debutloc) <= :today')
		    ->setParameter('today', date('Ym',strtotime($dEnd)))
		    ->andWhere('YEARMONTH(a.debutloc) >= :dtsart')
		    ->setParameter('dtsart', date('Ym',strtotime($dStart)))
		   ->orderBy('a.codemachineinterne', 'ASC');	
		}
		else
		{
			$qb = $this->createQueryBuilder('a');
			$qb->where('a.codemachineinterne = :search')
			->setParameter('search', $search) 
			->andwhere('YEARMONTH(a.debutloc) <= :today')
		    ->setParameter('today', date('Ym',strtotime($dEnd)))
		    ->andWhere('YEARMONTH(a.debutloc) >= :dtsart')
		    ->setParameter('dtsart', date('Ym',strtotime($dStart)))
		   ->orderBy('a.codemachineinterne', 'ASC');
		}
		return $qb->getQuery()->getResult();	
	}
	
